function isPeriod(e,r,t){var n=new Date(e).setHours(0,0,0,0),a=new Date(r).setHours(0,0,0,0),i=new Date(t).setHours(0,0,0,0);return i>=n&&i<=a}function getYear(){return(new Date).getFullYear()}function isCoronationDay(){var e=getYear();return isPeriod("05/01/"+e,"05/31/"+e,Date.now())}function isQueenBirthday(){var e=getYear();return isPeriod("06/01/"+e,"06/30/"+e,Date.now())}function isKingBirthday(){var e=getYear();return isPeriod("07/15/"+e,"07/31/"+e,Date.now())}function isMotherDay(){var e=getYear();return isPeriod("08/01/"+e,"08/15/"+e,Date.now())}function isFatherDay(){var e=getYear();return isPeriod("12/01/"+e,"12/15/"+e,Date.now())}function isNinthDeathDay(){var e=getYear();return isPeriod("10/01/"+e,"10/15/"+e,Date.now())}